'use strict';

import qs from './helpers/editcss';
import ajax from './helpers/ajax';
import e from './helpers/events';
import c from './helpers/editcss';

document.addEventListener('DOMContentLoaded', init);

/**
 * Inits navigation
 */
function init() {
  ajax('api/nav.json')
      .then(res => res.data)
      .then(data => renderTemplate(data.items));
}

/**
 * Renders navigation template on data arrival
 * @param  {Array} data Nav data
 */
function renderTemplate(data) {
  let nav = document.querySelector('#nav-content'),
      template = '',
      parentItems = '';

  for (let parent of data) {
    let children = '';

    // If contain children
    if (parent.items.length) {
      children = '<ul class="children">';
      for (let child of parent.items) {

        children += `<li><a href="${child.url}" target="_blank">${child.label}</a></li>`;
      }
      children += '</ul>';
    }

    parentItems +=`<li class="nav-content-item ${children ? 'has-children' : ''}"><a href="${parent.url}" target="_blank">${parent.label}</a> ${children}</li>`
  }

  template = `<ul>${parentItems}</ul>
    <footer>&copy; 2014 Huge. All Rights Reserved.</footer>`;

  nav.innerHTML = template;

  assignNavEvents();
}

/**
 * Bind DOM Events
 */
function assignNavEvents() {
  let $mainNav = c('.nav'),
      $navItems = e('.has-children > a'),
      eNavItems = c('.has-children'),
      $hamburger = e('.hamburger'),
      resizeInProgress = false;

  $hamburger.off().on('click', (e) => {
    $mainNav.toggleClass('open');
  });

  $navItems.off().on('click', (e) => {
    let target = e.target.nodeName === 'LI' ? c(e.target) : c(e.target.parentNode);
    e.preventDefault();
    c('.mask-backdrop').addClass('show');
    target.toggleClass('open');
  });

  e(window).on('resize', (e) => {
    if (!resizeInProgress) {
      resizeInProgress = true;

      window.setTimeout(() => {
        if ($mainNav.hasClass('open')) {
          $mainNav.removeClass('open');
        }

        if(eNavItems.hasClass('open')) {
          eNavItems.removeClass('open');
        }

        resizeInProgress = false;
      }, 500);
    }
  });

}

/**
 * Basic CSS class editor
 * @author Andrés Zorro <zorrodg@gmail.com>
 */
'use strict';

import * as u from './utils';

class EditCSS {
  constructor(selector) {
    this.SELECTORS = selector;
  }

  /**
   * Adds a new class to the element className string
   * @param {...spread} classNames classes to add
   */
  addClass(...classNames) {
    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i],
          classes = item.className.split(' ');
      classes = classes.concat(classNames);
      item.className = u.unique(classes).join(' ').trim();
    }

    return this;
  }

  /**
   * Removes a new class to the element className string
   * @param {...spread} classNames classes to remove
   */
  removeClass(...classNames) {
    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i],
          classes = item.className.split(' ');
      classes = classes.filter(it => classNames.indexOf(it) < 0);
      item.className = u.unique(classes).join(' ').trim();
    }

    return this;
  }

  /**
   * Check whether given elm has class
   * @param  {string}  className Name of the class
   * @return {Boolean}           true if found
   */
  hasClass(className) {
    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i],
          classes = item.className.split(' ');
      if (u.contains(classes, className)) {
        return true;
      }
    }

    return false;
  }

  /**
   * Turns class on/off
   * @param  {...spread} classNames classes to toggle
   */
  toggleClass(...classNames) {

    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i],
          classes = item ? item.className.split(' ') : [];

      for (let currentClass of classNames) {
        if (u.contains(classes, currentClass)) {
          classes = classes.filter(it => it !== currentClass);
        } else {
          classes.push(currentClass);
        }
      }

      item.className = u.unique(classes).join(' ').trim();
    }

    return this;
  }

  /**
   * Get/Set selector width
   * @param  {number} px target width (Leave empty to return current width)
   */
  width(px) {
    if (!px) {
      return Number.parseInt(this.SELECTORS[0].style.width.replace('px', ''));
    }
    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i];
      item.style.width = Number.parseInt(px) + 'px';
    }

    return this;
  }

  /**
   * Get/Set selector height
   * @param  {number} px target height (Leave empty to return current height)
   */
  height(px) {
    if (!px) {
      return Number.parseInt(this.SELECTORS[0].style.height.replace('px', ''));
    }
    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i];
      item.style.height = Number.parseInt(px) + 'px';
    }

    return this;
  }
}

export default function(selector) {
  if (selector instanceof HTMLElement) {
    return new EditCSS([selector]);
  }

  selector = selector instanceof NodeList ? selector : document.querySelectorAll(selector);

  if (!selector || !(selector instanceof NodeList)) {
    return;
  }

  return new EditCSS([].slice.call(selector, 0));
}

/**
 * Creates an AJAX call and returns a promise
 * Only GET method and JSON calls supported for now
 * @author Andrés Zorro <zorrodg@gmail.com>
 */
'use strict';

export default function(opts = {}) {
  return new Promise(function (resolve, reject) {
    let req;

    if (typeof opts === 'string') {
      opts = {
        url: opts
      };
    }

    // No AJAX without URL
    if (!opts || !opts.url) {
      throw new Error('No URL');
    }

    // Extend default options
    opts = Object.assign({
      method: 'GET',
      params: {},
      headers: {
        'Content-Type' : 'application/json'
      },
      qs: []
    }, opts);

    // Set Query String
    for (let p of Object.keys(opts.params)) {
      opts.qs.push(p + '=' + opts.params[p]);
    }

    // Set URL
    opts.url = opts.url + (opts.qs.length ? '?' + opts.qs.join('&') : '');
    delete opts.qs;

    // Create Request
    if (window.XMLHttpRequest) {
      req = new XMLHttpRequest();

      req.open(opts.method, opts.url, true);

      for (let i of Object.keys(opts.headers)) {
        req.setRequestHeader(i, opts.headers[i]);
      }

      // AJAX async response
      req.onreadystatechange = responseFn;

      // Sed query
      req.send(null);
    } else {
      throw new Error('No XMLHttpRequest');
    }

    /**
     * Executes on AJAX response
     */
    function responseFn() {
      if (req.readyState === 4) {
        try {
          // Success response
          if (req.status >= 200 && req.status < 400) {
            resolve({
              data: JSON.parse(req.responseText),
              status: req.status,
              config: Object.assign({}, opts)
            });
          } else {
            reject({
              error: req.responseText,
              status: req.status,
              config: Object.assign({}, opts)
            });
          }
        } catch (e) {
          // Error response
          reject({
            error: e,
            status: req.status,
            config: Object.assign({}, opts)
          });
        }  
      }
    }
  });
}

/**
 * Basic Event Listener support
 * @author Andrés Zorro <zorrodg@gmail.com>
 */
'use strict';

import * as u from './utils';

class EventRegister {

  constructor (selector) {
    this.events = {};
    this.SELECTORS = selector;
  }

  /**
   * Adds an event listener
   * @param  {string}   evt      Event name
   * @param  {Function} callback callback to execute
   * @param  {boolean} capture   event capture
   */
  on(evt, callback, capture = false) {
    if (!u.contains(Object.keys(this.events), evt)) {
      this.events[evt] = callback;
    }

    for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
      let item = this.SELECTORS[i];
      item.addEventListener(evt, this.events[evt], capture);
    }

    return this;
  }

  /**
   * Removes an event listener
   * @param  {string}   evt      Event name
   */
  off(evt) {
    // Remove all events
    if (!evt) {
      for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
        let item = this.SELECTORS[i];
        for (let evnt of Object.keys(this.events)) {
          item.removeEventListener(evnt, this.events[evnt]);
        }
      }
      this.events = {};
      return this;
    }

    // Remove specific event
    if (u.contains(Object.keys(this.events), evt)) {
      for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
        let item = this.SELECTORS[i];
        item.removeEventListener(evt, this.events[evt]);
      }

      delete this.events[evt];
    }

    return this;
  }

  /**
   * Triggers an event listener
   * @param  {string}   evt      Event name
   */
  trigger(evt) {
    if (u.contains(Object.keys(this.events), evt)) {
      for (let i = 0, len = this.SELECTORS.length; i < len; i++) {
        let item = this.SELECTORS[i],
            evtDispatcher = new Event(evt);

        item.dispatchEvent(evtDispatcher);
      }
    }

    return this;
  }
}

export default function (selector) {
  if (selector instanceof HTMLElement || selector instanceof Window) {
    return new EventRegister([selector]);
  }

  selector = selector instanceof NodeList ? selector : document.querySelectorAll(selector);

  if (!selector || !(selector instanceof NodeList)) {
    return;
  }

  return new EventRegister([].slice.call(selector, 0));
}

'use strict';

/**
 * Check if array contains item
 * @param  {Array}  arr  target
 * @param  {String} item
 * @return {Boolean}     true if found
 */
export function contains(arr = [], item = '') {
  for (let i of arr) {
    if (i === item) {
      return true;
    }
  }

  return false;
}

/**
 * Filters unique values
 * @param  {Array}  arr target
 * @return {Array}      filtered result
 */
export function unique(arr = []) {
  var result = [];

  for (let i of arr) {
    if (!contains(result, i)) {
      result.push(i);
    }
  }

  return result;
}
